/* C:\Work\07_Si\02_SimpleDMS_Page\frontend-react-new\src\pages\admin\Code.tsx */

import { useState } from "react";
import ICode from "../../types/admin/code/ICode";

function Code() {
  // todo: 변수 정의
  // todo: 객체 초기화
  const initialCode = {
    codeId: 0, // 공통코드ID
    codeName: "", // 공통코드명
    categoryId: 0, // 대분류코드ID
    categoryName: "", // 대분류코드명
    useYn: "",
  };

  // 저장객체
  const [code, setcode] = useState<ICode>(initialCode);
  // 저장버튼 클릭후 submitted = true 변경됨
  const [submitted, setSubmitted] = useState<boolean>(false);

  // todo: 함수 정의
  // todo: input 태그에 수동 바인딩
  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target; // 화면값
    setcode({ ...code, [name]: value }); // 변수저장
  };

  // 저장 함수
  const savecode = () => {
    // 임시 객체
    var data = {
      categoryId: code.categoryId, // 변수 = 화면값
      categoryName: code.categoryName,
    };

    CodeService.create(data) // 저장 요청
      .then((response: any) => {
        setSubmitted(true);
        console.log(response.data);
      })
      .catch((e: Error) => {
        console.log(e);
      });
  };

  // 새폼 보여주기 함수 : 변수값 변경 -> 화면 자동 갱신(리액트 특징)
  const newcode = () => {
    setcode(initialCode); // 초기화
    setSubmitted(false); // submitted 변수 초기화
  };

  return (
    <>
      {/* 제목 start */}
      <TitleCom title="Code Detail" />
      {/* 제목 end */}

      <>
        {code ? (
          <div className="col-6 mx-auto">
            <form>
              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="codeId" className="col-form-label">
                    codeId
                  </label>
                </div>

                <div className="col-9">
                  <input
                    type="text"
                    id="codeId"
                    disabled
                    className="form-control"
                    value={code.codeId}
                    onChange={handleInputChange}
                    placeholder="codeId"
                    name="codeId"
                  />
                </div>
              </div>
              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="codeName" className="col-form-label">
                    codeName
                  </label>
                </div>

                <div className="col-9">
                  <input
                    type="text"
                    id="codeName"
                    disabled
                    className="form-control"
                    value={code.codeName}
                    onChange={handleInputChange}
                    placeholder="codeName"
                    name="codeName"
                  />
                </div>
              </div>

              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="categoryId" className="col-form-label">
                    categoryId
                  </label>
                </div>

                <div className="col-9">
                  <input
                    type="text"
                    id="categoryId"
                    disabled
                    className="form-control"
                    value={code.categoryId}
                    onChange={handleInputChange}
                    placeholder="categoryId"
                    name="categoryId"
                  />
                </div>
              </div>

              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="unitPrice" className="col-form-label">
                    Use Y/N
                  </label>
                </div>
                <div className="col-9">
                  <select
                    className="form-select form-select-sm"
                    aria-label=".form-select-sm example"
                    id="useYn"
                    value={code.useYn}
                    onChange={handleSelectChange}
                    name="useYn"
                  >
                    <option value="Y">사용함</option>
                    <option value="N">사용 안함</option>
                  </select>
                </div>
              </div>
            </form>

            <div className="row g-3 mt-3 mb-3">
              <button
                type="submit"
                onClick={updateCode}
                className="btn btn-outline-success ms-2 col"
              >
                Update
              </button>
            </div>

            <p>{message}</p>
          </div>
        ) : (
          <div className="col-6 mx-auto">
            <br />
            <p>Please click on a CodeNop...</p>
          </div>
        )}
      </>
    </>
  );
}

export default Code;
