// AddQna.tsx : rfce

import React, { useState } from "react";
import TitleCom from "../../../components/common/TitleCom";
import IQna from "../../../types/basic/IQna";

function AddQna() {
  // 객체 초기화
  const initialDept = {
    qno: null,
    question: "",
    answer: "",
    questioner: "",
    answerer: "",
  };

  // qna 객체
  const [qna, setQna] = useState<IQna>(initialDept);
  // 저장버튼 클릭후 submitted = true 변경됨
  const [submitted, setSubmitted] = useState<boolean>(false);

  // input 태그에 수동 바인딩
  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target; // 화면값
    setQna({ ...qna, [name]: value }); // 변수저장
  };

  // 저장 함수
  const saveDept = () => {
    // 임시 부서 객체
    var data = {
      dname: dept.dname,
      loc: dept.loc,
    };

    DeptService.create(data) // 저장 요청
      .then((response: any) => {
        setSubmitted(true);
        console.log(response.data);
      })
      .catch((e: Error) => {
        console.log(e);
      });
  };

  // 새폼 보여주기 함수 : 변수값 변경 -> 화면 자동 갱신(리액트 특징)
  const newDept = () => {
    setDept(initialDept); // 부서 초기화
    setSubmitted(false); // submitted 변수 초기화
  };
  return (
    // 여기
    <div className="row">
      {submitted ? (
        <div className="col-6 mx-auto">
          <h4>You submitted successfully!</h4>
          <button className="btn btn-success" onClick={newDept}>
            Add
          </button>
        </div>
      ) : (
        <>
          {/* 제목 start */}
          <TitleCom title="Add Qna" />
          {/* 제목 end */}

          <div className="col-6 mx-auto">
            {/* question 입력창 시작 */}
            <div className="row g-3 align-items-center mb-3">
              <div className="col-3">
                {/* 라벨 시작 */}
                <label htmlFor="question" className="col-form-label">
                  Question
                </label>
                {/* 라벨 끝 */}
              </div>
              <div className="col-9">
                <input
                  type="text"
                  id="question"
                  required
                  className="form-control"
                  value={qna.question}
                  onChange={handleInputChange}
                  placeholder="question"
                  name="question"
                />
              </div>
            </div>
            {/* question 입력창 끝 */}

            {/* questioner 입력창 시작 */}
            <div className="row g-3 align-items-center mb-3">
              <div className="col-3">
                {/* 라벨 시작 */}
                <label htmlFor="questioner" className="col-form-label">
                  Questioner
                </label>
                {/* 라벨 끝 */}
              </div>
              <div className="col-9">
                <input
                  type="text"
                  id="questioner"
                  required
                  className="form-control"
                  value={qna.questioner}
                  onChange={handleInputChange}
                  placeholder="questioner"
                  name="questioner"
                />
              </div>
            </div>
            {/* questioner 입력창 끝 */}

            {/* answer 입력창 시작 */}
            <div className="row g-3 align-items-center mb-3">
              <div className="col-3">
                {/* 라벨 시작 */}
                <label htmlFor="answer" className="col-form-label">
                  Answer
                </label>
                {/* 라벨 끝 */}
              </div>
              <div className="col-9">
                <input
                  type="text"
                  id="answer"
                  required
                  className="form-control"
                  value={qna.answer}
                  onChange={handleInputChange}
                  placeholder="answer"
                  name="answer"
                />
              </div>
            </div>
            {/* answer 입력창 끝 */}

            {/* answerer 입력창 시작 */}
            <div className="row g-3 align-items-center mb-3">
              <div className="col-3">
                {/* 라벨 시작 */}
                <label htmlFor="answerer" className="col-form-label">
                  Answerer
                </label>
                {/* 라벨 끝 */}
              </div>
              <div className="col-9">
                <input
                  type="text"
                  id="answerer"
                  required
                  className="form-control"
                  value={qna.answerer}
                  onChange={handleInputChange}
                  placeholder="answerer"
                  name="answerer"
                />
              </div>
            </div>
            {/* answerer 입력창 끝 */}

            {/* 저장 버튼 시작 */}
            <div className="row g-3 mt-3 mb-3">
              <button
                onClick={saveQna}
                className="btn btn-outline-primary ms-2 col"
              >
                Submit
              </button>
            </div>
            {/* 저장 버튼 끝 */}
          </div>
        </>
      )}
    </div>
  );
}

export default AddQna;
