// AddCinemaCinemaFaq.tsx : rfce

import React, { useState } from 'react'
import TitleCom from '../../../components/common/TitleCom';

function AddCinemaFaq() {
     // TODO: 변수 정의
  const initialFaq = {
    no: null,
    title: "",
    content: "",
  };

  // cinemaFaq객체
  const [cinemaFaq, setCinemaFaq] = useState<IFaq>(initialFaq);
  // 저장버튼 클릭후 submitted = true 변경됨
  const [submitted, setSubmitted] = useState<boolean>(false);

  // TODO: 함수 정의
  // input 태그에 수동 바인딩
  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target; // 화면값
    setCinemaFaq({ ...cinemaFaq, [name]: value }); // 변수저장
  };
  // 저장 함수
  const saveCinemaFaq = () => {
    // 임시 객체
    var data = {
        title: cinemaFaq.title,
        content: cinemaFaq.content
    };

    CinemaFaqService.create(data) // 저장 요청
      .then((response: any) => {
        setSubmitted(true);
        console.log(response.data);
      })
      .catch((e: Error) => {
        console.log(e);
      });
  };

  // 새폼 보여주기 함수 : 변수값 변경 -> 화면 자동 갱신(리액트 특징)
  const newCinemaFaq = () => {
    setCinemaFaq(initialCinemaFaq); // cinemaFaq 초기화
    setSubmitted(false); // submitted 변수 초기화
  };

  return (
      // 여기
      <div className="row">
      {submitted ? (
        <div className="col-6 mx-auto">
          <h4>You submitted successfully!</h4>
          <button className="btn btn-success" onClick={newCinemaFaq}>
            Add
          </button>
        </div>
      ) : (
        <>
          {/* 제목 start */}
          <TitleCom title="Add CinemaFaq" />
          {/* 제목 end */}

          <div className="col-6 mx-auto">
            <div className="row g-3 align-items-center mb-3">
              <div className="col-3">
                <label htmlFor="title" className="col-form-label">
                  Title
                </label>
              </div>

              <div className="col-9">
                <input
                  type="text"
                  id="title"
                  required
                  className="form-control"
                  value={cinemaFaq.title}
                  onChange={handleInputChange}
                  placeholder="title"
                  name="title"
                />
              </div>
            </div>

            <div className="row g-3 align-items-center mb-3">
              <div className="col-3">
                <label htmlFor="content" className="col-form-label">
                  Content
                </label>
              </div>
              <div className="col-9">
                <input
                  type="text"
                  id="content"
                  required
                  className="form-control"
                  value={cinemaFaq.content}
                  onChange={handleInputChange}
                  placeholder="content"
                  name="content"
                />
              </div>
            </div>

            <div className="row g-3 mt-3 mb-3">
              <button
                onClick={saveCinemaFaq}
                className="btn btn-outline-primary ms-2 col"
              >
                Submit
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  )
}

export default AddCinemaCinemaFaq
