// Faq.tsx : rfce

import { useState } from "react";
import TitleCom from "../../../components/common/TitleCom";
import { useNavigate, useParams } from "react-router-dom";

function Faq() {

  // TODO: 변수 정의
  // 전체조회 페이지에서 전송한 기본키(dno)
  const { no } = useParams();
  // 강제페이지 이동 함수
  let navigate = useNavigate();

  // 객체 초기화(상세조회 : 기본키 있음)
  const initialFaq = {
    no: null,
    title: "",
    content: ""
  };

  // 수정될객체
  const [faq, setFaq] = useState<IFaq>(initialFaq);
  // 화면에 수정 성공에 메세지 찍기 변수
  const [message, setMessage] = useState<string>("");
    // 함수 정의

  return (
    <>
      {/* 제목 start */}
      <TitleCom title="Faq Detail" />
      {/* 제목 end */}

      <>
        {faq ? (
          <div className="col-6 mx-auto">
            <form>
              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="title" className="col-form-label">
                    Title
                  </label>
                </div>

                <div className="col-9">
                  <input
                    type="text"
                    id="title"
                    required
                    className="form-control"
                    value={Faq.title}
                    onChange={handleInputChange}
                    placeholder="title"
                    name="title"
                  />
                </div>
              </div>

              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="content" className="col-form-label">
                    Content
                  </label>
                </div>

                <div className="col-9">
                  <input
                    type="text"
                    id="content"
                    required
                    className="form-control"
                    value={faq.content}
                    onChange={handleInputChange}
                    placeholder="content"
                    name="content"
                  />
                </div>
              </div>
            </form>

            <div className="row g-3 mt-3 mb-3">
              <button
                onClick={deleteFaq}
                className="btn btn-outline-danger ms-3 col"
              >
                Delete
              </button>

              <button
                type="submit"
                onClick={updateFaq}
                className="btn btn-outline-success ms-2 col"
              >
                Update
              </button>
            </div>

            {message && (
              <p className="alert alert-success mt-3 text-center">{message}</p>
            )}
          </div>
        ) : (
          <div className="col-6 mx-auto">
            <br />
            <p>Please click on a Faq...</p>
          </div>
        )}
      </>
    </>
  );
}

export default Faq;
