/* C:\Work\07_Si\02_SimpleDMS_Page\frontend-react-new\src\pages\shop\thema-load\ThemaLoadList.tsx */
// 공공 데이터 포털 : 부산 테마거리 전체 조회
// TODO: import
import React from "react";
import TitleCom from "../../../components/common/TitleCom";
import { Pagination } from "@mui/material";

function ThemaLoadList() {
    // TODO: 변수 정의
    
    // TODO: 함수 정의
    
  return (
    // TODO: JSX
    <>
      {/* 제목 start */}
      <TitleCom title="ThemaLoad List" />
      {/* 제목 end */}

      {/* paging 시작 */}
      <div className="mt-3">
        {"Items per Page: "}
        <select onChange={handlePageSizeChange} value={pageSize}>
          {pageSizes.map((size) => (
            <option key={size} value={size}>
              {size}
            </option>
          ))}
        </select>

        <Pagination
          className="my-3"
          count={count}
          page={page}
          siblingCount={1}
          boundaryCount={1}
          variant="outlined"
          shape="rounded"
          onChange={handlePageChange}
        />
      </div>
      {/* paging 끝 */}

      {/* table start */}
      <div className="col-md-12">
        {/* table start */}
        <table className="table">
          <thead className="table-light">
            <tr className="text-center">
              <th scope="col">식당(ID)</th>
              <th scope="col">식당명</th>
              <th scope="col">지역명</th>
              <th scope="col">식당이미지(ID)</th>
              <th scope="col">식당이미지(URL)</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody>
            {themaLoad &&
              themaLoad.map((data, index) => (
                <tr key={index} className="text-center">
                  <td>{data["식당(ID)"]}</td>
                  <td>{data["식당명"]}</td>
                  <td>{data["지역명"]}</td>
                  <td>{data["식당이미지(ID)"]}</td>
                  <td>
                    <img
                      src={data["식당이미지(URL)"]}
                      width="50vw"
                      height="30vh"
                    />{" "}
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
        {/* table end */}
      </div>
      {/* table end */}
    </>
  );
}

export default ThemaLoadList;
