// AddCustomer.tsx : rfce

import React, { useState } from 'react'
import TitleCom from '../../../components/common/TitleCom';
import ICustomer from '../../../types/basic/ICustomer';
import CustomerService from '../../../services/basic/CustomerService';

function AddCustomer() {
   // 객체 초기화
   const initialCustomer = {
    cid: null,
    fullName: "",
    email: "",
    phone: ""
  };

  // customer객체
  const [customer, setCustomer] = useState<ICustomer>(initialCustomer);
  // 저장버튼 클릭후 submitted = true 변경됨
  const [submitted, setSubmitted] = useState<boolean>(false);

  // input 태그에 수동 바인딩
  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target; // 화면값
    setCustomer({ ...customer, [name]: value }); // 변수저장
  };

  // 저장 함수
  const saveCustomer = () => {
    // 임시 customer 객체
    var data = {
      cid: customer.cid,
      fullName: customer.fullName,
      email: customer.email,
      phone: customer.phone,
    };

    CustomerService.create(data) // 저장 요청
      .then((response: any) => {
        setSubmitted(true);
        console.log(response.data);
      })
      .catch((e: Error) => {
        console.log(e);
      });
  };

  // 새폼 보여주기 함수 : 변수값 변경 -> 화면 자동 갱신(리액트 특징)
  const newCustomer = () => {
    setCustomer(initialCustomer); // Customer 초기화
    setSubmitted(false); // submitted 변수 초기화
  };

    return (
      // 여기
      <div className="row">
        {submitted ? (
          <div className="col-6 mx-auto">
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={newCustomer}>
              Add
            </button>
          </div>
        ) : (
          <>
            {/* 제목 start */}
            <TitleCom title="Add Customer" />
            {/* 제목 end */}
  
            <div className="col-6 mx-auto">
              {/* question 입력창 시작 */}
              <div className="row g-3 align-items-center mb-3">
                {/* 라벨 시작 */}
                <div className="col-3">
                  <label htmlFor="question" className="col-form-label">
                    Question
                  </label>
                </div>
                {/* 라벨 끝 */}
  
                <div className="col-9">
                  <input
                    type="text"
                    id="question"
                    required
                    className="form-control"
                    value={customer.question}
                    onChange={handleInputChange}
                    placeholder="question"
                    name="question"
                  />
                </div>
              </div>
              {/* question 입력창 끝 */}
  
              {/* questioner 입력창 시작 */}
              <div className="row g-3 align-items-center mb-3">
                {/* 라벨 시작 */}
                <div className="col-3">
                  <label htmlFor="questioner" className="col-form-label">
                    Questioner
                  </label>
                </div>
                {/* 라벨 끝 */}
  
                <div className="col-9">
                  <input
                    type="text"
                    id="questioner"
                    required
                    className="form-control"
                    value={customer.questioner}
                    onChange={handleInputChange}
                    placeholder="questioner"
                    name="questioner"
                  />
                </div>
              </div>
              {/* questioner 입력창 끝 */}
  
              {/* answer 입력창 시작 */}
              <div className="row g-3 align-items-center mb-3">
                {/* 라벨 시작 */}
                <div className="col-3">
                  <label htmlFor="answer" className="col-form-label">
                    Answer
                  </label>
                </div>
                {/* 라벨 끝 */}
  
                <div className="col-9">
                  <input
                    type="text"
                    id="answer"
                    required
                    className="form-control"
                    value={customer.answer}
                    onChange={handleInputChange}
                    placeholder="answer"
                    name="answer"
                  />
                </div>
              </div>
              {/* answer 입력창 끝 */}
  
              {/* answerer 입력창 시작 */}
              <div className="row g-3 align-items-center mb-3">
                {/* 라벨 시작 */}
                <div className="col-3">
                  <label htmlFor="answerer" className="col-form-label">
                    Answerer
                  </label>
                </div>
                {/* 라벨 끝 */}
  
                <div className="col-9">
                  <input
                    type="text"
                    id="answerer"
                    required
                    className="form-control"
                    value={customer.answerer}
                    onChange={handleInputChange}
                    placeholder="answerer"
                    name="answerer"
                  />
                </div>
              </div>
              {/* answerer 입력창 끝 */}
  
              {/* 저장 버튼 시작 */}
              <div className="row g-3 mt-3 mb-3">
                <button
                  onClick={saveCustomer}
                  className="btn btn-outline-primary ms-2 col"
                >
                  Submit
                </button>
              </div>
              {/* 저장 버튼 끝 */}
            </div>
          </>
        )}
      </div>
    );
}

export default AddCustomer
