import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import IDept from "../../../types/basic/IDept";
import TitleCom from "../../../components/common/TitleCom";
import DeptService from "../../../services/basic/DeptService";

function Dept() {

  const { dno } = useParams();
  let navigate = useNavigate();

  const initialDept = {
    dno: "",
    dname: "",
    loc: "",
  };

  const [dept, setDept] = useState<IDept>(initialDept);
  const [message, setMessage] = useState<string>("");

  const getDept = (dno: string) => {
    DeptService.get(dno)
      .then((response: any) => {
        setDept(response.data);
        console.log(response.data);
      })
      .catch((e: Error) => {
        console.log(e);
      });
  };

  useEffect(() => {
    if (dno) getDept(dno);
  }, [dno]);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setDept({ ...dept, [name]: value });
  };

  const updateDept = () => {
    DeptService.update(dept.dno, dept)
      .then((response: any) => {
        console.log(response.data);
        setMessage("The dept was updated successfully!");
      })
      .catch((e: Error) => {
        console.log(e);
      });
  };

  const deleteDept = () => {
    DeptService.remove(dept.dno)
      .then((response: any) => {
        console.log(response.data);
        // 페이지 이동
        navigate("/dept");
      })
      .catch((e: Error) => {
        console.log(e);
      });
  };

  return (
    <>
      {/* 제목 start */}
      <TitleCom title="Dept Detail" />
      {/* 제목 end */}

      <>
        {dept ? (
          <div className="col-6 mx-auto">
            <form>
              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="dname" className="col-form-label">
                    Dname
                  </label>
                </div>

                <div className="col-9">
                  <input
                    type="text"
                    id="dname"
                    required
                    className="form-control"
                    value={dept.dname}
                    onChange={handleInputChange}
                    placeholder="dname"
                    name="dname"
                  />
                </div>
              </div>

              <div className="row g-3 align-items-center mb-3">
                <div className="col-3">
                  <label htmlFor="loc" className="col-form-label">
                    Loc
                  </label>
                </div>

                <div className="col-9">
                  <input
                    type="text"
                    id="loc"
                    required
                    className="form-control"
                    value={dept.loc}
                    onChange={handleInputChange}
                    placeholder="loc"
                    name="loc"
                  />
                </div>
              </div>
            </form>

            <div className="row g-3 mt-3 mb-3">
              <button
                onClick={deleteDept}
                className="btn btn-outline-danger ms-3 col"
              >
                Delete
              </button>

              <button
                type="submit"
                onClick={updateDept}
                className="btn btn-outline-success ms-2 col"
              >
                Update
              </button>
            </div>

            {message && (
              <p className="alert alert-success mt-3 text-center">{message}</p>
            )}
          </div>
        ) : (
          <div className="col-6 mx-auto">
            <br />
            <p>Please click on a Dept...</p>
          </div>
        )}
      </>
    </>
  );
}

export default Dept;
