/* C:\Work\07_Si\02_SimpleDMS_Page\frontend-react-new\src\pages\advanced\FileDbList.tsx */
// 업로드 전체 조회
// TODO: import
import React from 'react'
import TitleCom from '../../components/common/TitleCom';
import { Pagination } from '@mui/material';

function FileDbList() {
  return (
    <>
    {/* 제목 start */}
    <TitleCom title="FileDb List" />
    {/* 제목 end */}

    <div className="col-md-8">
      {/* 검색어 start */}
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search by title"
          value={searchTitle}
          onChange={onChangeSearchTitle}
        />
        <div className="input-group-append">
          <button
            className="btn btn-outline-secondary"
            type="button"
            onClick={retrieveFileDb}
          >
            Search
          </button>
        </div>
      </div>
      {/* 검색어 end */}
    </div>

    <div className="col-md-12 mt-3">
      <h4>FileDb List</h4>
      {/* page start */}
      <div className="mt-3">
        {"Items per Page: "}
        <select onChange={handlePageSizeChange} value={pageSize}>
          {pageSizes.map((size) => (
            <option key={size} value={size}>
              {size}
            </option>
          ))}
        </select>

        <Pagination
          className="my-3"
          count={count}
          page={page}
          siblingCount={1}
          boundaryCount={1}
          variant="outlined"
          shape="rounded"
          onChange={handlePageChange}
        />
      </div>
      {/* page end */}

      {/* upload 성공/실패 메세지 출력 시작 */}
      {message && (
        <div className="alert alert-success" role="alert">
          {message}
        </div>
      )}
      {/* upload 성공/실패 메세지 출력 끝 */}

      {/* 쇼핑카트 이미지 start */}
      <div className="row">
        {fileDb &&
          fileDb.map((data, index) => (
            <div className="col-sm-6" key={index}>
              <div className="card">
                <img src={data.fileUrl} className="card-img-top" alt="강의" />
                <div className="card-body">
                  <h5 className="card-title">{data.fileTitle}</h5>
                  <p className="card-text">{data.fileContent}</p>
                  <Link to={"/fileDb/" + data.uuid}>
                    <span className="badge bg-warning">Edit</span>
                  </Link>
                  <a
                    style={{ color: "inherit" }}
                    className="ms-2"
                    onClick={() => {
                      deleteImage(data.uuid);
                    }}
                  >
                    <span className="badge bg-danger">Delete</span>
                  </a>
                </div>
              </div>
            </div>
          ))}
      </div>
      {/* 쇼핑카트 이미지 end */}
    </div>
  </>
  )
}

export default FileDbList
